// A flat list of all words for the spinning animation
export const wordList: string[] = [
    ...categorizedWords.noun,
    ...categorizedWords.verb,
    ...categorizedWords.adjective,
    ...categorizedWords.adverb
];

/**
 * Shuffles an array in place using the Fisher-Yates algorithm.
 * @param array The array to shuffle.
 * @returns The shuffled array.
 */
const shuffleArray = <T>(array: T[]): T[] => {
  const newArray = [...array];
  for (let i = newArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [newArray[i], newArray[j]] = [newArray[j], newArray[i]]; // Swap elements
  }
  return newArray;
};


export const generateWords = (count: number, type: WordType = 'any'): string[] => {
  let sourceList: string[];

  if (type === 'any') {
    sourceList = wordList;
  } else {
    sourceList = categorizedWords[type];
  }

  const shuffled = shuffleArray(sourceList);
  return shuffled.slice(0, count);
};
